mapper.select:
select userid, userpw, username
from tbl_member
where userid = #{userId}
and userpw = #{userPw}

mapper.update:
update tbl_member set
userpw = #{userPw},
username = #{userName}
where userid = #{userId}

mapper.delete:
delete from tbl_member
where userid = #{userId}
and userpw = #{userPw}

mapper.select:
select count(*) from tbl_member
where userid = #{userId}
and userpw = #{userPw}
